import React from "react";
import { View, StyleSheet } from "react-native";
import PlayerCard from "./playercards/PlayerCard";
import addBackgroundColor from "../helper/addBackgroundColor";
import ThreePlayerCard from './playercards/ThreePlayerCard';

const GameScreen = (props) => {
  const { numPlayers, lifePoints, layout } = props.navigation.state.params;

  const playerCards = (() => {
    let players = [];

    for (let i = 1; i <= numPlayers; i++) {
      players.push(i);
    }

    if (numPlayers <= 2) {
      return players.map(player => (
        <PlayerCard
          key={player}
          player={player}
          lifePoints={lifePoints}
          layout={layout}
          playerBackgroundColor={addBackgroundColor()}
        />
      ));
    } else if (numPlayers === 3) {
      return players.map(player => (
        <ThreePlayerCard
          key={player}
          player={player}
          lifePoints={lifePoints}
          playerBackgroundColor={addBackgroundColor()}
        />
      ));
    }
  })();

  return <View style={[
    styles.container,
    styles.threePlayer,
  ]}>{playerCards}</View>;
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'flex-start',
    backgroundColor: '#000'
  },
  threePlayer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
  }
});

export default GameScreen;
