import React, { useState } from "react";
import { View, Text, TouchableOpacity, StyleSheet } from "react-native";
import { AntDesign } from "@expo/vector-icons";
import useLongPress from "../../helper/useLongPress";

const ThreePlayerCard = (props) => {
  const { player, lifePoints, layout, playerBackgroundColor } = props;
  const [playerLifePoints, setPlayerLifePoints] = useState(lifePoints);

  const [width, setWidth] = useState();
  const [height, setHeight] = useState();

  const setGameLayout = () => {
    if (player === 1 || player === 2) {
      return styles.twoColumnFlex;
    }
  };

  const getComponentDimensions = (event) => {
    let { width, height } = event.nativeEvent.layout;
    setWidth(width);
    setHeight(height);
  };

  //Setup absolute position and size of +/- buttons
  const setButtonPosition = (position) => {
    if (player == 1 && layout === "180deg") {
      return position === "top"
        ? styles.buttonAbsoluteBottom
        : styles.buttonAbsoluteTop;
    } else if (player == 2 && layout === "180deg") {
      return position === "top"
        ? styles.buttonAbsoluteTop
        : styles.buttonAbsoluteBottom;
    } else {
      return position === "top"
        ? styles.buttonSideBySidePlus
        : styles.buttonSideBySideMinus;
    }
  };

  const setButtonSize = () => {
    if (layout === "180deg") {
      return {
        width,
        height: height / 2,
      };
    } else {
      return {
        width: width / 2,
        height,
      };
    }
  };

  //Counter Functionality
  const addLife = () => {
    setPlayerLifePoints(playerLifePoints + 1);
  };

  const loseLife = () => {
    playerLifePoints > 0 && setPlayerLifePoints(playerLifePoints - 1);
  };

  return (
    <View style={[
      styles.container,
      player < 3 && { flex: 2 },
      player == 3 && { flex: 1 }]}
    >
      <View
        onLayout={getComponentDimensions}
        style={[
          styles.playerCard,
          // player > 1 && styles.marginTop,
          playerBackgroundColor,
          player == 1 && { marginRight: 10 },
          player == 3 && { marginTop: 10, flexBasis: '34%' },
          player < 3 && { flexBasis: '66%' }
        ]}
      >
        <View >
          <View style={styles.center}>
            <Text style={styles.text}>{playerLifePoints}</Text>
            <AntDesign name="heart" size={30} color="#000" />
            {/* <Text>{player}</Text> */}
          </View>
        </View>
        {/* <TouchableOpacity
          style={[
            setButtonPosition("top"),
            width && setButtonSize(),
            styles.button,
          ]}
          onPress={() => addLife()}
          {...useLongPress(addLife)}
        />
        <TouchableOpacity
          style={[
            setButtonPosition("bottom"),
            width && setButtonSize(),
            styles.button,
          ]}
          onPress={() => loseLife()}
          {...useLongPress(loseLife)}
        /> */}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flexBasis: '50%',
  },
  playerCard: {
    borderRadius: 10,
    paddingBottom: 20
  },
  twoColumnFlex: {

  },
  sideBySideOne: {
    flex: 2,
    transform: [{ rotate: "90deg" }],
  },
  sideBySideTwo: {
    flex: 2,
    transform: [{ rotate: "-90deg" }],
  },
  text: {
    fontSize: 150,
    letterSpacing: 0.6,
    fontWeight: "100",
    fontFamily: "Helvetica",
  },
  button: {
    // flex: 1,
    borderRadius: 10,
    position: "absolute",
    backgroundColor: "#fff",
    opacity: 0,
  },
  center: {
    justifyContent: "center",
    alignItems: "center",
  },
  buttonAbsoluteTop: {
    top: 0,
  },
  buttonAbsoluteBottom: {
    bottom: 0,
  },
  buttonSideBySidePlus: {
    left: 0,
  },
  buttonSideBySideMinus: {
    right: 0,
  },
  marginTop: {
    marginTop: 10,
  },
});

export default ThreePlayerCard;
